properties.entry:
SELECT APP.APPROVAL_SEQUENCE AS APPROVAL_SEQUENCE , DECODE ( :paramLocale ,'ar',A_CODENM,CODES.CODENM
) AS APPROVER_ROLE_NAME
FROM APPROVAL_MASTER APP , SIS_CODES CODES , SIS_OBJECT OBJ WHERE APP.FORMCD = OBJ.OBJECTCD AND APP.APPROVER_ROLECD = CODES.SISCODECD AND OBJ.OBJECTNM = :paramFormName ORDER BY APP.APPROVAL_SEQUENCE
